example.code:
                    
                $("#demo").dialog({
    buttonSet : {
        "确定" : function()
        {
            console.debug("确定!!!");
            return false;
        },
        "取消" : function()
        {
            console.debug("取消!!!");
        }
    }
});

                    
                $("#demo").dialog({
    position : "center",
    draggable : true,
    dragStart : function()
    {
        //console.debug("dragStart");
    },
    draging : function()
    {
        //console.debug("draging");
    },
    dragStop : function()
    {
        console.debug("dragStop");
    }
});

                    
                $("#demo").dialog({
    position : "center",
    resizable : true,
    resizeStart : function()
    {
        //console.debug("resizeStart");
    },
    resizing : function()
    {
        //console.debug("resizing");
    },
    resizeStop : function()
    {
        console.debug("resizeStop");
    }
});

                    
                $("#demo").dialog({
    destroyOnClose : false
});
window.globalTimer = setTimeout(function()
{
    $("#demo").dialog("close");
    window.globalTimer = setTimeout(function()
    {
        $("#demo").dialog("open");        
    }, 2000);
}, 1000);

                    
                $("#demo").menu({
    url : "./menu.json",
    click : function()
    {
        console.debug("click");
        return false;
    }
});

                    
                $("#demo").tabs();
$("#demo").tabs("item", 0, "内容修改", "标题修改");
$("#demo").tabs("item", 1, "内容修改1");
